function findSvtNameZh(searchId)
{
	var i;
	for(i=0;i<svtName.length;i++)
		if(svtName[i][0]==searchId) return svtName[i][1];
	for(i in master.mstSvt)
		if(master.mstSvt[i].id==searchId) return master.mstSvt[i].name;
	for(i in mstClass)
		if(Math.floor(searchId/10000)==mstClass[i].id) return "未知"+mstClass[i].name+searchId;
	return null;
}
